syntax = "proto3";

package sso;
option go_package = "sso_local.v1;sso";

// Определение сервиса аутентификации
service SSOService {
  // Метод для входа в систему
  rpc SignIn (SignInRequest) returns (SignInResponse);
}

// Структура запроса для входа
message SignInRequest {
  int32 password = 1; // Пароль пользователя
  int32 app_id = 2; // ID приложения

}

// Структура ответа после успешного входа
message SignInResponse {
  string access_token = 1; // Токен доступа
}

syntax = "proto3";

package app_provider;
option go_package = "app_provider_local.v1;app_provider";

service AppProviderService {
  rpc Create (CreateRequest) returns (CreateResponse);
  rpc App (GetAppRequest) returns (GetAppResponse);
  rpc Apps (GetAppsRequest) returns (GetAppsResponse);
  rpc Update (UpdateRequest) returns (UpdateResponse);
  rpc Delete (DeleteRequest) returns (DeleteResponse);
}

message App {
  int32 id = 1;
  string name = 2;
  string secret = 3;
}

message CreateRequest {
  string name = 1;
  string secret = 2;
}

message CreateResponse {
  int32 id = 1;
}

message GetAppRequest {
  int32 id = 1;
}

message GetAppResponse {
  App app = 1;
}

message GetAppsRequest {}

message GetAppsResponse {
  repeated App apps = 1;
}

message UpdateRequest {
  int32 id = 1;
  string name = 2;
  string secret = 3;
}

message UpdateResponse {
  bool success = 1;
}

message DeleteRequest {
  int32 id = 1;
}

message DeleteResponse {
  bool success = 1;
}

syntax = "proto3";

package statuses;
option go_package = "statuses_local.v1;statuses";

// Статус продукции
message StatusResponse {
  int32 id = 1;
  string name = 2;
  string description = 3; // Описание статуса (опционально)
}

// CRUD методы для статусов
service StatusService {
  rpc Create(CreateStatusRequest) returns (StatusResponse);
  rpc Status(GetStatusRequest) returns (StatusResponse);
  rpc Update(UpdateStatusRequest) returns (StatusResponse);
  rpc Delete(DeleteStatusRequest) returns (DeleteStatusResponse);
  rpc List(ListStatusesRequest) returns (StatusListResponse);
}

// Запрос на создание статуса
message CreateStatusRequest {
  string name = 1;
  string description = 2;
}

// Запрос на получение статуса
message GetStatusRequest {
  int32 id = 1;
}

// Запрос на обновление статуса
message UpdateStatusRequest {
  int32 id = 1;
  string name = 2;
  string description = 3;
}

// Запрос на удаление статуса
message DeleteStatusRequest {
  int32 id = 1;
}

// Ответ на удаление статуса
message DeleteStatusResponse {
  bool success = 1; // true, если удаление успешно
}

// Пустой запрос и ответ для получения списка статусов
message ListStatusesRequest {}
message StatusListResponse {
  repeated StatusResponse data = 1;
}

syntax = "proto3";

package locations;
