message LocationListResponse {
  repeated LocationResponse data = 1;
}

syntax = "proto3";

package movements;
option go_package = "movements_local.v1;movements";

import "google/protobuf/timestamp.proto";

// Перемещение продукции
message MovementResponse {
  int64 id = 1;
  int64 product_id = 2;
  int32 from_location_id = 3;
  int32 to_location_id = 4;
  int32 user_id = 5;
  string comment = 6;
  google.protobuf.Timestamp created_at = 7;
  google.protobuf.Timestamp removed_at = 8;
}


// CRUD методы для перемещений
service MovementService {
  rpc Create(CreateMovementRequest) returns (MovementResponse);
  rpc Movement(GetMovementRequest) returns (MovementResponse);
  rpc Update(UpdateMovementRequest) returns (MovementResponse);
  rpc Delete(DeleteMovementRequest) returns (DeleteMovementResponse);
  rpc List(ListMovementsRequest) returns (MovementListResponse);
}

// Запрос на создание перемещения
message CreateMovementRequest {
  int64 product_id = 1;
  int32 from_location_id = 2;
  int32 to_location_id = 3;
  int32 user_id = 4;
  string comment = 5;
}

// Запрос на получение перемещения
message GetMovementRequest {
  int64 id = 1;
}

// Запрос на обновление перемещения
message UpdateMovementRequest {
  int64 id = 1;
  int64 product_id = 2;
  int32 from_location_id = 3;
  int32 to_location_id = 4;
  int32 user_id = 5;
  string comment = 6;
}

message DeleteMovementRequest {
  int64 id = 1;
}
message DeleteMovementResponse {
  bool success = 1; // true, если удаление успешно
}

// Пустой запрос и ответ для получения списка перемещений
message ListMovementsRequest {
  int64 product_id = 1;
}
message MovementListResponse {
  repeated MovementResponse data = 1;
}

syntax = "proto3";

package products_sk_statuses;
option go_package = "products_sk_statuses_local.v1;products_sk_statuses";

import "google/protobuf/timestamp.proto";

// Связь продукта со статусами
message ProductSkStatusResponse {
  int64 product_id = 1;
  int32 status_id = 2;
  bool active = 3; // Активный статус
  google.protobuf.Timestamp created_at = 4;
}

// CRUD методы для статусов продукции
service ProductStatusService {
  rpc Create(CreateProductStatusRequest) returns (ProductSkStatusResponse);
  rpc ProductSkStatus(GetProductStatusRequest) returns (ProductSkStatusResponse);
  rpc Update(UpdateProductStatusRequest) returns (ProductSkStatusResponse);
  rpc Delete(DeleteProductStatusRequest) returns (DeleteProductStatusResponse);
  rpc List(ListProductStatusesRequest) returns (ProductStatusListResponse);
}

// Запрос на создание статуса продукции
message CreateProductStatusRequest {
  int64 product_id = 1;
  int32 status_id = 2;
}

// Запрос на получение статуса продукции
message GetProductStatusRequest {
  int64 product_id = 1;
  int32 status_id = 2;
}

// Запрос на обновление статуса продукции
message UpdateProductStatusRequest {
  int64 product_id = 1;
  int32 status_id = 2;
  bool active = 3;
}

// Запрос на удаление статуса продукции
message DeleteProductStatusRequest {
  int64 product_id = 1;
  int32 status_id = 2;
}
message DeleteProductStatusResponse {
  bool success = 1; // true, если удаление успешно
}

// Пустой запрос для получения списка статусов продукции
message ListProductStatusesRequest {}
message ProductStatusListResponse {
  repeated ProductSkStatusResponse data = 1;
}

syntax = "proto3";
package products_sk;
option go_package = "products_sk_local.v1;products_sk";

import "google/protobuf/timestamp.proto";

message ProductResponse {
  int64 id = 1;
  string part_name = 2;
  string nomenclature = 3;
  int32 number_frame = 4;
  float weight_sp_kg = 5;
  float weight_gp_kg = 6;
  google.protobuf.Timestamp manufacturing_date = 7;
}

// CRUD методы для продукции
service ProductService {
  rpc Create(CreateProductRequest) returns (ProductResponse);
  rpc Product(GetProductRequest) returns (ProductResponse);
  rpc Update(UpdateProductRequest) returns (ProductResponse);
  rpc Delete(DeleteProductRequest) returns (DeleteProductResponse);
  rpc List(ListProductsRequest) returns (ProductListResponse);
  rpc ProductsByStatus(ProductsByStatusRequest) returns (ProductListResponse);
}

// Запрос на создание продукции
message CreateProductRequest {
  int64 id = 1;
  string part_name = 2;
  string nomenclature = 3;
  int32 number_frame = 4;
  float weight_sp_kg = 5;
  float weight_gp_kg = 6;
  google.protobuf.Timestamp manufacturing_date = 7;
}

// Запрос на получение продукции
message GetProductRequest {
  int64 id = 1;
}

// Запрос на обновление продукции
message UpdateProductRequest {
  int64 id = 1;
  string part_name = 2;
  string nomenclature = 3;
  int32 number_frame = 4;
  float weight_sp_kg = 5;
  float weight_gp_kg = 6;
  google.protobuf.Timestamp manufacturing_date = 7;
}

// Запрос на удаление продукции
message DeleteProductRequest {
  int64 id = 1;
}

message DeleteProductResponse {
  bool success = 1;
}

// Пустой запрос для получения списка продукции
message ListProductsRequest {}

message ProductListResponse {
  repeated ProductResponse data = 1;
}

// Запрос для получения продукции по статусу
message ProductsByStatusRequest {
  int32 status_id = 1; 
}

syntax = "proto3";
package production_task;
option go_package = "production_task_local.v1;production_task";

import "google/protobuf/timestamp.proto";

// Request parameters for tasks
message RequestTaskParams {
  string part_name = 1; 
  int32 skl_id = 2;
  google.protobuf.Timestamp date_start = 3;
  google.protobuf.Timestamp date_end = 4;
  string search = 5;
  int32 page = 6;
  int32 count = 7;
}

// CRUD методы для для заданий производства
service ProductionTaskService {
  rpc GetTasks(RequestTaskParams) returns (ProductsResponse);
  rpc GetTasksInPartName(RequestTaskParams) returns (ProductsResponse);
  rpc RecordInMsSQL(IDsRequest) returns (EmptyResponse);
  rpc RecordOutMsSQL(IDsRequest) returns (EmptyResponse);
}

// Product model
message Product {
  int32 id = 1;
  google.protobuf.Timestamp manufacturing_date = 2;
  string nomenclature = 3;
  int32 number_frame = 4;
  string part_name = 5;
  double weight_sp_kg = 6;
  double weight_gp_kg = 7;
}

// Response with list of products
message ProductsResponse {
  repeated Product products = 1;
}

// IDs request
message IDsRequest {
  repeated int32 ids = 1;
}

message EmptyResponse {}
